import { useCallback } from 'react'
import { useWallet } from 'use-wallet'
import { Contract } from 'web3-eth-contract'
import useQuic from './useQuic'
import BigNumber from 'bignumber.js'
import { mint, getQuicContract } from '../quic/utils'

const useUpdateCap = () => {

  const { account } = useWallet()
  const quic = useQuic()
	const quicContract = getQuicContract(quic)

  const handleUpdateCap = useCallback(
    async (amount: string) => {
      console.log("Updating the Cap to " + amount)  
      const txHash = await mint(quicContract, account, recipient, new BigNumber(parseInt(amount)))
      console.log(txHash)
      return txHash
    }, [quicContract])

  return { onUpdateCap: handleUpdateCap}
}

export default useUpdateCap
