import React, { createContext, useEffect, useState } from 'react'

import { useWallet } from 'use-wallet'

import { Quic } from '../../quic'

export interface QuicContext {
	quic?: typeof Quic
}

export const Context = createContext<QuicContext>({
	quic: undefined,
})

declare global {
	interface Window {
		quicsauce: any
		quic: any
	}
}

const BaoProvider: React.FC = ({ children }) => {
	const { ethereum }: { ethereum: any } = useWallet()
	const [quic, setBao] = useState<any>()

	window.quic = quic

	useEffect(() => {
		if (ethereum) {
			const chainId = Number(ethereum.chainId)
			console.log(chainId)
			const baoLib = new Quic(ethereum, chainId, false, {
				defaultAccount: ethereum.selectedAddress,
				defaultConfirmations: 1,
				autoGasMultiplier: 1.05,
				testing: false,
				defaultGas: '300000',
				defaultGasPrice: '20000000000',
				accounts: [],
				ethereumNodeTimeout: 10000,
			})
			console.log(baoLib)
			setQuic(quicLib)
			window.quicsauce = quicLib
		}
	}, [ethereum])

	return <Context.Provider value={{ bao }}>{children}</Context.Provider>
}

export default QuicProvider
