import BigNumber from 'bignumber.js'
import React, { useCallback, useMemo, useState } from 'react'
import Button from '../../../components/Button'
import Modal, { ModalProps } from '../../../components/Modal'
import ModalActions from '../../../components/ModalActions'
import ModalTitle from '../../../components/ModalTitle'

import styled from 'styled-components'
import Input, { InputProps } from '../../../components/Input'

import { getQuicContract } from '../../../quic/utils'

interface MintModalProps extends ModalProps {
	amount: BigNumber
	onConfirm: (address: string, amount: BigNumber) => void
	recipientAddress?: string
}

const MintModal: React.FC<MintModalProps> = ({
	onConfirm,
	onDismiss,
}) => {
	const [amount, setAmount] = useState('')
	const [recipientAddress, setRecipientAddress] = useState('')
	const [pendingTx, setPendingTx] = useState(false)
	const quicContract = getQuicContract(quic);

	const handleAmountChange = useCallback(
		(e: React.FormEvent<HTMLInputElement>) => {
			setAmount(e.currentTarget.value)
		},
		[setAmount],
	)

	const handleAddressChange = useCallback(
		(e: React.FormEvent<HTMLInputElement>) => {
			setRecipientAddress(e.currentTarget.value)
		},
		[setRecipientAddress],
	)

	return (
		<Modal>
			<ModalTitle text={`Mint QUIC Tokens`} />
			<Input 
				endAdornment={
					<StyledTokenAdornmentWrapper>
						<StyledTokenSymbol>Amount </StyledTokenSymbol>
						<StyledSpacer />

					</StyledTokenAdornmentWrapper>
				}
				onChange={handleAmountChange}
				placeholder="10000"
				value={amount.toString()}/>
			<Input 
				endAdornment={
					<StyledTokenAdornmentWrapper>
						<StyledTokenSymbol>Address </StyledTokenSymbol>
						<StyledSpacer />

					</StyledTokenAdornmentWrapper>
				}
				onChange={handleAddressChange}
				placeholder="0xf49eA18Ca30372d8A21D9Ee9e33B1fa7efb7AaE5"
				value={recipientAddress}/>
			<ModalActions>
				<Button text="Cancel" variant="secondary" onClick={onDismiss} />
				<Button
					disabled={pendingTx}
					text={pendingTx ? 'Pending Confirmation' : 'Confirm'}
					onClick={async () => {
						setPendingTx(true)
						await onConfirm(recipientAddress, new BigNumber(parseInt(amount)))
						setPendingTx(false)
						onDismiss()
					}}
				/>
			</ModalActions>
		</Modal>
	)
}

const StyledTokenInput = styled.div``

const StyledSpacer = styled.div`
	width: ${(props) => props.theme.spacing[3]}px;
`

const StyledTokenAdornmentWrapper = styled.div`
	align-items: center;
	display: flex;
`

const StyledMaxText = styled.div`
	align-items: center;
	color: ${(props) => props.theme.color.grey[400]};
	display: flex;
	font-size: 14px;
	font-weight: 700;
	height: 44px;
	justify-content: flex-end;
`

const StyledTokenSymbol = styled.span`
	color: ${(props) => props.theme.color.grey[600]};
	font-weight: 700;
`

export default MintModal
