import { useCallback } from 'react'
import { useWallet } from 'use-wallet'
import { Contract } from 'web3-eth-contract'
import useQuic from './useQuic'
import BigNumber from 'bignumber.js'
import { addPair, getQuicContract } from '../quic/utils'

const useAddPair = () => {

  const { account } = useWallet()
  const quic = useQuic()
  const masterChefContract = getMasterChefContract(quic)


  const handleMint = useCallback(
    async (weight: string, lpAddress: string) => {
      console.log("Adding " + lpAddress + " with weight:" + weight)  
      const txHash = await addPair(masterChefContract, account, new BigNumber(parseInt(weight)), lpAddress)
      console.log(txHash)
      return txHash
    }, [masterChefContract])

  return { onMint: handleMint}
}

export default useAddPair
