import React, { useEffect, useCallback, useState, Fragment } from 'react'
import { Route, Switch, useRouteMatch } from 'react-router-dom'
import { useWallet } from 'use-wallet'
import WalletProviderModal from '../../components/WalletProviderModal'
import styled from 'styled-components'
import useModal from '../../hooks/useModal'
import Card from '../../components/Card'
import CardContent from '../../components/CardContent'
import Label from '../../components/Label'
import Spacer from '../../components/Spacer'
import QuicIcon from '../../components/QuicIcon'
import useAllEarnings from '../../hooks/useAllEarnings'
import useAllStakedValue from '../../hooks/useAllStakedValue'
import useFarms from '../../hooks/useFarms'
import useTokenBalance from '../../hooks/useTokenBalance'
import Value from '../../components/Value'

import Button from '../../components/Button'
import Page from '../../components/Page'

import { getMasterChefContract, getQuicContract, getCap } from '../../quic/utils'
import useQuic from '../../hooks/useQuic'
import useRunSetup from '../../hooks/useRunSetup'
import useMint from '../../hooks/useMint'
import useCapUpdate from '../../hooks/useCapUpdate'

import MintModal from './components/MintModal'
import CapUpdateModal from './components/CapUpdateModal'

const Admin: React.FC = () => {
	const [supplyCap, setSupplyCap] = useState<BigNumber>()
	const { path } = useRouteMatch()
	const { account } = useWallet()
	const [onPresentWalletProviderModal] = useModal(<WalletProviderModal />)
    const quic = useQuic()

    const chef = getMasterChefContract(quic)
	const [pendingTx, setPendingTx] = useState(false)

    const { onRunSetup } = useRunSetup()
	const { onMint } = useMint()
	const { onCapUpdate } = useCapUpdate()

    const [onPresentMint] = useModal(
		<MintModal
			onConfirm={onMint}
		/>,
	)

	const [onPresentCapUpdate] = useModal(
		<CapUpdateModal
			onConfirm={onCapUpdate}
		/>,
	)

	useEffect(() => {
		async function fetchSupplyCap() {
			const supply = await getCap(quic)
			setSupplyCap(supply)
		}
		if (quic) {
			fetchSupplyCap()
		}
	}, [quic, setSupplyCap])




	return (
		<Switch>
			<Page>
				{account && account ===  '0xf49eA18Ca30372d8A21D9Ee9e33B1fa7efb7AaE5' ? (
					<>  
					<Fragment>
						<StyledWrapper>
							<Card>
								<CardContent>
									<StyledBalances>
										<StyledBalance>
											<QuicIcon />
											<Spacer />
											<div style={{ flex: 1 }}>
												<Label text="Quic Cap" />
												<Value
													value={account ? getBalanceNumber(quicBalance) : 'Locked'}
												/>
											</div>
										</StyledBalance>
									</StyledBalances>
								</CardContent>
								<Footnote>
									Pending harvest
									<FootnoteValue>
										<PendingRewards /> QUIC
									</FootnoteValue>
								</Footnote>
							</Card>
							<Spacer />

							<Card>
								<CardContent>
									<Label text="Total QUIC Supply" />
									<Value
										value={totalSupply ? getBalanceNumber(totalSupply) : 'Locked'}
									/>
								</CardContent>
								<Footnote>
									New rewards per block
									<FootnoteValue>1000 QUIC</FootnoteValue>
								</Footnote>
							</Card>
						</StyledWrapper>
					</Fragment>
                        Hello Admin 
                        <div
                            style={{
                                alignItems: 'center',
                                display: 'flex',
                                flex: 1,
                                justifyContent: 'center',
                            }}>

                            <Button
                            						text={pendingTx ? 'Setting up ......' : 'Run Setup'}
                                                    onClick={async () => {
                                                        setPendingTx(true)
                                                        await onRunSetup()
                                                        setPendingTx(false)
                                                    }}
                                                    />

                                <StyledActionSpacer />
								<Button 
									text="Mint"
									onClick={onPresentMint}/>
								<StyledActionSpacer />
								<Button 
									text="Update Cap"
									onClick={onPresentCapUpdate}/>
                        </div>
					</>
				) : (
					<div
						style={{
							alignItems: 'center',
							display: 'flex',
							flex: 1,
							justifyContent: 'center',
						}}
					>
						<Button
							onClick={onPresentWalletProviderModal}
							text="🔓 Unlock Wallet"
						/>
					</div>
				)}
			</Page>
		</Switch>

	)
}

const Footnote = styled.div`
	font-size: 14px;
	padding: 8px 20px;
	color: ${(props) => props.theme.color.grey[400]};
	border-top: solid 1px ${(props) => props.theme.color.grey[300]};
`
const FootnoteValue = styled.div`
	font-family: 'Roboto Mono', monospace;
	float: right;
`

const StyledWrapper = styled.div`
	align-items: center;
	display: flex;
	@media (max-width: 768px) {
		width: 100%;
		flex-flow: column nowrap;
		align-items: stretch;
	}
`

const StyledBalances = styled.div`
	display: flex;
`

const StyledBalance = styled.div`
	align-items: center;
	display: flex;
	flex: 1;
`

const StyledActionSpacer = styled.div`
	height: ${(props) => props.theme.spacing[4]}px;
	width: ${(props) => props.theme.spacing[4]}px;
`

export default Admin
