import { useCallback } from 'react'
import { useWallet } from 'use-wallet'
import { Contract } from 'web3-eth-contract'
import useQuic from './useQuic'
import BigNumber from 'bignumber.js'
import { mint, getQuicContract } from '../quic/utils'

const useMint = () => {

  const { account } = useWallet()
  const quic = useQuic()
	const quicContract = getQuicContract(quic)

  const handleMint = useCallback(async () => {
    console.log("Minting " + amount + " to " + recipient)  
    const txHash = await mint(quicContract, account, recipient, amount)
    console.log(txHash)
    return txHash
  }, [quicContract, recipient, amount])

  return { onMint: handleMint}
}

export default useMint
