import BigNumber from 'bignumber.js'
import React, { useEffect, useState } from 'react'
import Countdown from 'react-countdown'
import type { CountdownRenderProps } from 'react-countdown'
import styled, { keyframes } from 'styled-components'
import { useWallet } from 'use-wallet'
import Button from '../../../components/Button'
import Card from '../../../components/Card'
import CardContent from '../../../components/CardContent'
import CardIcon from '../../../components/CardIcon'
import Loader from '../../../components/Loader'
import Spacer from '../../../components/Spacer'
import { Farm } from '../../../contexts/Farms'
import useAllStakedValue, {
	StakedValue,
} from '../../../hooks/useAllStakedValue'
import useFarms from '../../../hooks/useFarms'
import useQuic from '../../../hooks/useQuic'
import { getEarned, getMasterChefContract } from '../../../quic/utils'
import { bnToDec } from '../../../utils'
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import './tab-styles.css';
import { PoolType } from '../../../contexts/Farms/types'


interface FarmWithStakedValue extends Farm, StakedValue {
	apy: BigNumber
}

const cardsPerRow = 3;

const FarmCards: React.FC = () => {
	const [farms] = useFarms()
	const { account } = useWallet()
	const stakedValue = useAllStakedValue()

	const quicIndex = farms.findIndex(({ tokenSymbol }) => tokenSymbol === 'QUIC')

	const quicPrice =
		quicIndex >= 0 && stakedValue[quicIndex]
			? stakedValue[quicIndex].tokenPriceInWeth
			: new BigNumber(0)

	const BLOCKS_PER_YEAR = new BigNumber(2336000)
	const BAO_BER_BLOCK = new BigNumber(64000)

	const pools: {[key: string]: FarmWithStakedValue[]} = {
		[PoolType.UNI]: [],
		[PoolType.SUSHI]: [],
		[PoolType.ARCHIVED]: []
	};

	farms.forEach(
		(farm, i) => {
			const farmWithStakedValue = {
				...farm,
				...stakedValue[i],
				poolType: farm.poolType || PoolType.UNI,
				apy: stakedValue[i]
					? quicPrice
							.times(BAO_BER_BLOCK)
							.times(BLOCKS_PER_YEAR)
							.times(stakedValue[i].poolWeight)
							.div(stakedValue[i].totalWethValue)
					: null,
			};

			pools[farmWithStakedValue.poolType].push(farmWithStakedValue);
		}
	);

	return (
		<Tabs>
			<TabList>
				<Tab>Uni Pools</Tab>
				<Tab>Sushi Pools</Tab>
				<Tab>Archived Pools</Tab>
			</TabList>

			<TabPanel>
				<StyledCards>
					{pools[PoolType.UNI].length ? (
						pools[PoolType.UNI].map((farm, i) => (
							<React.Fragment key={i}>
								<FarmCard farm={farm} />
								{((i + 1) % (cardsPerRow) !== 0) && <StyledSpacer />}
							</React.Fragment>
						))
					) : (
							<StyledLoadingWrapper>
								<Loader text="Cooking the rice ..." />
							</StyledLoadingWrapper>
						)}
				</StyledCards>
			</TabPanel>
			<TabPanel>
				<StyledCards>
					{pools[PoolType.SUSHI].length ? (
						pools[PoolType.SUSHI].map((farm, i) => (
							<React.Fragment key={i}>
								<FarmCard farm={farm} />
								{((i + 1) % (cardsPerRow) !== 0) && <StyledSpacer />}
							</React.Fragment>
						))
					) : (
							<StyledLoadingWrapper>
								<Loader text="Cooking the rice ..." />
							</StyledLoadingWrapper>
						)}
				</StyledCards>
			</TabPanel>
			<TabPanel>
				<StyledCards>
					{pools[PoolType.ARCHIVED].length ? (
						pools[PoolType.ARCHIVED].map((farm, i) => (
							<React.Fragment key={i}>
								<FarmCard farm={farm} />
								{((i + 1) % (cardsPerRow) !== 0) && <StyledSpacer />}
							</React.Fragment>
						))
					) : (
							<StyledLoadingWrapper>
								<Loader text="Cooking the rice ..." />
							</StyledLoadingWrapper>
						)}
				</StyledCards>
			</TabPanel>
		</Tabs>
	);
}

interface FarmCardProps {
	farm: FarmWithStakedValue
}

const FarmCard: React.FC<FarmCardProps> = ({ farm }) => {
	const [startTime, setStartTime] = useState(0)
	const [harvestable, setHarvestable] = useState(0)

	const { account } = useWallet()
	const { lpTokenAddress } = farm
	const quic = useBao()

	const renderer = (countdownProps: CountdownRenderProps) => {
		const { hours, minutes, seconds } = countdownProps
		const paddedSeconds = seconds < 10 ? `0${seconds}` : seconds
		const paddedMinutes = minutes < 10 ? `0${minutes}` : minutes
		const paddedHours = hours < 10 ? `0${hours}` : hours
		return (
			<span style={{ width: '100%' }}>
				{paddedHours}:{paddedMinutes}:{paddedSeconds}
			</span>
		)
	}

	useEffect(() => {
		async function fetchEarned() {
			if (quic) return
			const earned = await getEarned(
				getMasterChefContract(quic),
				lpTokenAddress,
				account,
			)
			setHarvestable(bnToDec(earned))
		}
		if (quic && account) {
			fetchEarned()
		}
	}, [quic, lpTokenAddress, account, setHarvestable])

	const poolActive = true // startTime * 1000 - Date.now() <= 0
	const tokenBuy = 'Buy ' + farm.tokenSymbol
	const destination = farm.refUrl
	return (
		<StyledCardWrapper>
			{farm.tokenSymbol === 'BAO' && <StyledCardAccent />}
			<Card>
				<CardContent>
					<StyledContent>
						<CardIcon>
							<img src={farm.icon} alt="" height="50" />
						</CardIcon>
						<StyledTitle>{farm.name}</StyledTitle>
						<StyledDetails>
							<StyledDetail>Deposit {farm.lpToken.toUpperCase()}</StyledDetail>
							<StyledDetail>Earn {farm.earnToken.toUpperCase()}</StyledDetail>
						</StyledDetails>
						<Spacer />
						<Button
							disabled={!poolActive}
							text={poolActive ? 'Select' : undefined}
							to={`/farms/${farm.id}`}
						>
							{!poolActive && (
								<Countdown
									date={new Date(startTime * 1000)}
									renderer={renderer}
								/>
							)}
						</Button>
						<Spacer />
						<Button text={tokenBuy} href={destination}></Button>
						<StyledInsight>
							<span>APY</span>
							<span>
								{farm.apy
									? `${farm.apy
											.times(new BigNumber(100))
											.toNumber()
											.toLocaleString('en-US')
											.slice(0, -1)}%`
									: 'Loading ...'}
							</span>
							{/* <span>
                {farm.tokenAmount
                  ? (farm.tokenAmount.toNumber() || 0).toLocaleString('en-US')
                  : '-'}{' '}
                {farm.tokenSymbol}
              </span>
              <span>
                {farm.wethAmount
                  ? (farm.wethAmount.toNumber() || 0).toLocaleString('en-US')
                  : '-'}{' '}
                ETH
              </span> */}
						</StyledInsight>
